#' Example 3
#'
#' 
#' @param data .
#' @param dependent .
#' @param group .
#' @param equVar .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$ttest} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$ttest$asDF}
#'
#' \code{as.data.frame(results$ttest)}
#'
#' @export
Example03 <- function(
    data,
    dependent,
    group,
    equVar = TRUE) {

    if ( ! requireNamespace('jmvcore'))
        stop('Example03 requires jmvcore to be installed (restart may be required)')

    if ( ! missing(dependent)) dependent <- jmvcore::resolveQuo(jmvcore::enquo(dependent))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dependent), dependent, NULL),
            `if`( ! missing(group), group, NULL))


    options <- Example03Options$new(
        dependent = dependent,
        group = group,
        equVar = equVar)

    analysis <- Example03Class$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
